import RPi.GPIO as GPIOimport timeimport datetimeGPIO.setmode(GPIO.BCM) # GPIO Nummern statt Board Nummernx=datetime.datetime.now()while True:    try:        logfile=open('logrichtig.txt', 'a')        RELAIS_1_GPIO = 22        GPIO.setup(RELAIS_1_GPIO, GPIO.OUT) # GPIO Modus zuweisen        GPIO.output(RELAIS_1_GPIO, GPIO.HIGH) # an        logfile.write('Bewaesserung:'+ x.strftime("%c")+'\n')        print(x.strftime("%c"))        time.sleep(10)        GPIO.output(RELAIS_1_GPIO, GPIO.LOW) # aus        logfile.close()             time.sleep(86400)    except KeyboardInterrupt:        print('Programm beendet')        GPIO.cleanup()        logfile.write('Programm beendet \n')        logfile.close()